/**
 * @title CrossEra Reward System
 * @notice Event-driven reward system for CrossFi applications with minimal on-chain storage
 * @dev Uses comprehensive events for data indexing and off-chain analytics
 */
contract CrossEraRewardSystem is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // ==================== ROLES ====================
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");
    bytes32 public constant CAMPAIGN_MANAGER_ROLE = keccak256("CAMPAIGN_MANAGER_ROLE");

    // ==================== ENUMS ====================
    enum CampaignStatus {
        Pending,    // Created but not started
        Active,     // Currently running
        Ended,      // Time expired, awaiting finalization
        Finalized,  // Rewards calculated, ready for claims
        Completed   // All rewards claimed
    }

    enum TransactionType {
        Transfer,
        ContractCall,
        DeFiInteraction,
        NFTTransaction,
        Other
    }

    // ==================== STRUCTS ====================
    struct CampaignCore {
        bool exists;
        bool active;
        uint64 startDate;
        uint64 endDate;
        uint256 totalPool;
        uint256 distributedRewards;
        CampaignStatus status;
    }

    // ==================== STATE VARIABLES ====================

    // Critical on-chain storage only
    mapping(string => address) public appOwners;              // app_id -> developer address
    mapping(string => bool) public registeredApps;            // app_id -> registration status
    mapping(bytes32 => bool) public processedTransactions;    // tx_hash -> processed status
    mapping(uint32 => CampaignCore) public campaigns;         // campaign_id -> campaign core data
    mapping(string => mapping(uint32 => uint256)) public claimableRewards; // app_id -> campaign_id -> claimable amount

    // Counters for validation and indexing
    uint32 public totalCampaigns;
    uint32 public totalApps;
    uint32 public totalDevelopers;

    // Token configuration
    IERC20 public rewardToken;
    uint256 public minRewardAmount = 0.1 ether; // 0.1 XFI minimum reward

    // ==================== EVENTS ====================

    // App Management Events
    event AppRegistered(
        string indexed appId,
        address indexed developer,
        string appName,
        string description,
        string category,
        string websiteUrl,
        bytes32 metadataHash,
        uint256 timestamp
    );

    event AppUpdated(
        string indexed appId,
        address indexed developer,
        string newName,
        string newDescription,
        string newCategory,
        string newWebsiteUrl,
        uint256 timestamp
    );

    event AppStatusChanged(
        string indexed appId,
        address indexed developer,
        uint8 status, // 0=active, 1=paused, 2=deprecated
        string reason,
        uint256 timestamp
    );

    // Campaign Management Events
    event CampaignCreated(
        uint32 indexed campaignId,
        address indexed creator,
        string name,
        string description,
        uint256 totalPool,
        uint64 startDate,
        uint64 endDate,
        uint8 rewardType, // 0=proportional, 1=fixed, 2=tiered
        bytes32 rulesHash,
        uint256 timestamp
    );

    event CampaignStatusChanged(
        uint32 indexed campaignId,
        CampaignStatus indexed newStatus,
        CampaignStatus oldStatus,
        address changedBy,
        uint256 timestamp
    );

    event AppRegisteredForCampaign(
        string indexed appId,
        uint32 indexed campaignId,
        address indexed developer,
        uint256 registrationFee,
        bytes32 registrationData,
        uint256 timestamp
    );

    event AppUnregisteredFromCampaign(
        string indexed appId,
        uint32 indexed campaignId,
        address indexed developer,
        string reason,
        uint256 timestamp
    );

    // Transaction & Reward Events
    event TransactionProcessed(
        string indexed appId,
        uint32 indexed campaignId,
        bytes32 indexed txHash,
        address developer,
        uint256 gasUsed,
        uint256 gasPrice,
        uint256 transactionValue,
        uint256 feeGenerated,
        uint256 rewardCalculated,
        TransactionType transactionType,
        bytes32 additionalData,
        uint256 timestamp
    );

    event RewardCalculated(
        string indexed appId,
        uint32 indexed campaignId,
        address indexed developer,
        uint256 totalContribution,
        uint256 campaignTotalContribution,
        uint256 rewardAmount,
        uint16 contributionPercentage, // Basis points (10000 = 100%)
        uint256 timestamp
    );

    event RewardClaimed(
        string indexed appId,
        uint32 indexed campaignId,
        address indexed developer,
        uint256 amount,
        uint256 totalClaimedToDate,
        bytes32 claimTxHash,
        uint256 timestamp
    );

    event BatchRewardsProcessed(
        uint32 indexed campaignId,
        uint32 batchNumber,
        uint32 appsProcessed,
        uint256 totalRewardsDistributed,
        bytes32 batchHash,
        uint256 timestamp
    );

    // Analytics Events
    event AppPerformanceSnapshot(
        string indexed appId,
        uint32 indexed campaignId,
        uint32 snapshotPeriod, // 0=daily, 1=weekly, 2=monthly
        uint256 totalTransactions,
        uint256 totalVolume,
        uint256 totalFees,
        uint256 totalRewards,
        uint256 averageTransactionValue,
        uint16 marketShareBasisPoints,
        uint256 timestamp
    );

    event DeveloperStatsUpdated(
        address indexed developer,
        uint32 totalApps,
        uint32 activeCampaigns,
        uint256 lifetimeRewards,
        uint256 lifetimeTransactions,
        uint256 lifetimeVolume,
        uint32 rankingPosition,
        uint256 timestamp
    );

    event PlatformStatsSnapshot(
        uint32 totalDevelopers,
        uint32 totalApps,
        uint32 activeCampaigns,
        uint256 totalRewardsDistributed,
        uint256 totalTransactionsProcessed,
        uint256 totalVolumeProcessed,
        uint256 platformTVL,
        uint256 timestamp
    );

    // Governance Events
    event ConfigurationUpdated(
        bytes32 indexed configKey,
        bytes32 oldValue,
        bytes32 newValue,
        address updatedBy,
        uint256 timestamp
    );

    event EmergencyAction(
        uint8 indexed actionType, // 0=pause, 1=unpause, 2=emergency_withdraw
        address indexed executor,
        string reason,
        bytes32 additionalData,
        uint256 timestamp
    );

    // ==================== MODIFIERS ====================

    modifier onlyAdmin() {
        require(hasRole(ADMIN_ROLE, msg.sender), "CrossEra: Not admin");
        _;
    }

    modifier onlyVerifier() {
        require(hasRole(VERIFIER_ROLE, msg.sender), "CrossEra: Not verifier");
        _;
    }

    modifier onlyCampaignManager() {
        require(hasRole(CAMPAIGN_MANAGER_ROLE, msg.sender), "CrossEra: Not campaign manager");
        _;
    }

    modifier appExists(string memory appId) {
        require(registeredApps[appId], "CrossEra: App not registered");
        _;
    }

    modifier appOwnerOnly(string memory appId) {
        require(appOwners[appId] == msg.sender, "CrossEra: Not app owner");
        _;
    }

    modifier campaignExists(uint32 campaignId) {
        require(campaigns[campaignId].exists, "CrossEra: Campaign not found");
        _;
    }

    // ==================== CONSTRUCTOR ====================

    constructor(address _rewardToken, address _admin) {
        require(_rewardToken != address(0), "CrossEra: Invalid token address");
        require(_admin != address(0), "CrossEra: Invalid admin address");

        rewardToken = IERC20(_rewardToken);

        // Setup roles
        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(ADMIN_ROLE, _admin);
        _grantRole(VERIFIER_ROLE, _admin); // Admin can also verify initially
        _grantRole(CAMPAIGN_MANAGER_ROLE, _admin);
    }

    // ==================== APP MANAGEMENT FUNCTIONS ====================

    /**
     * @notice Register a new application
     * @param appId Unique application identifier
     * @param appName Human-readable application name
     * @param description Application description
     * @param category Application category
     * @param websiteUrl Application website URL
     */
    function registerApp(
        string memory appId,
        string memory appName,
        string memory description,
        string memory category,
        string memory websiteUrl
    ) external whenNotPaused {
        require(bytes(appId).length > 0 && bytes(appId).length <= 32, "CrossEra: Invalid app ID length");
        require(bytes(appName).length > 0 && bytes(appName).length <= 64, "CrossEra: Invalid app name length");
        require(!registeredApps[appId], "CrossEra: App already registered");
        require(appOwners[appId] == address(0), "CrossEra: App ID taken");

        // Minimal on-chain storage
        appOwners[appId] = msg.sender;
        registeredApps[appId] = true;
        totalApps++;

        // Check if this is developer's first app
        bool isNewDeveloper = true; // In production, you'd check if developer has other apps
        if (isNewDeveloper) {
            totalDevelopers++;
        }

        // Comprehensive event emission for off-chain indexing
        bytes32 metadataHash = keccak256(abi.encodePacked(description, category, websiteUrl));

        emit AppRegistered(
            appId,
            msg.sender,
            appName,
            description,
            category,
            websiteUrl,
            metadataHash,
            block.timestamp
        );
    }

    /**
     * @notice Update application information
     * @param appId Application identifier
     * @param newName New application name
     * @param newDescription New application description
     * @param newCategory New application category
     * @param newWebsiteUrl New application website URL
     */
    function updateApp(
        string memory appId,
        string memory newName,
        string memory newDescription,
        string memory newCategory,
        string memory newWebsiteUrl
    ) external appExists(appId) appOwnerOnly(appId) whenNotPaused {
        require(bytes(newName).length > 0 && bytes(newName).length <= 64, "CrossEra: Invalid app name length");

        emit AppUpdated(
            appId,
            msg.sender,
            newName,
            newDescription,
            newCategory,
            newWebsiteUrl,
            block.timestamp
        );
    }

    /**
     * @notice Change application status
     * @param appId Application identifier
     * @param status New status (0=active, 1=paused, 2=deprecated)
     * @param reason Reason for status change
     */
    function changeAppStatus(
        string memory appId,
        uint8 status,
        string memory reason
    ) external appExists(appId) appOwnerOnly(appId) whenNotPaused {
        require(status <= 2, "CrossEra: Invalid status");

        emit AppStatusChanged(
            appId,
            msg.sender,
            status,
            reason,
            block.timestamp
        );
    }

    // ==================== CAMPAIGN MANAGEMENT FUNCTIONS ====================

    /**
     * @notice Create a new campaign
     * @param name Campaign name
     * @param description Campaign description
     * @param totalPool Total reward pool amount
     * @param startDate Campaign start timestamp
     * @param endDate Campaign end timestamp
     * @param rewardType Reward distribution type (0=proportional, 1=fixed, 2=tiered)
     * @param rulesHash IPFS hash of detailed campaign rules
     */
    function createCampaign(
        string memory name,
        string memory description,
        uint256 totalPool,
        uint64 startDate,
        uint64 endDate,
        uint8 rewardType,
        bytes32 rulesHash
    ) external onlyCampaignManager whenNotPaused returns (uint32) {
        require(bytes(name).length > 0 && bytes(name).length <= 64, "CrossEra: Invalid campaign name");
        require(totalPool > 0, "CrossEra: Invalid pool amount");
        require(startDate > block.timestamp, "CrossEra: Start date must be in future");
        require(endDate > startDate, "CrossEra: End date must be after start date");
        require(rewardType <= 2, "CrossEra: Invalid reward type");

        totalCampaigns++;
        uint32 campaignId = totalCampaigns;

        // Minimal on-chain storage
        campaigns[campaignId] = CampaignCore({
            exists: true,
            active: false,
            startDate: startDate,
            endDate: endDate,
            totalPool: totalPool,
            distributedRewards: 0,
            status: CampaignStatus.Pending
        });

        // Transfer reward pool to contract
        rewardToken.safeTransferFrom(msg.sender, address(this), totalPool);

        // Comprehensive event emission
        emit CampaignCreated(
            campaignId,
            msg.sender,
            name,
            description,
            totalPool,
            startDate,
            endDate,
            rewardType,
            rulesHash,
            block.timestamp
        );

        return campaignId;
    }

    /**
     * @notice Activate a campaign
     * @param campaignId Campaign identifier
     */
    function activateCampaign(uint32 campaignId) 
        external 
        onlyCampaignManager 
        campaignExists(campaignId) 
        whenNotPaused 
    {
        CampaignCore storage campaign = campaigns[campaignId];
        require(campaign.status == CampaignStatus.Pending, "CrossEra: Campaign not pending");
        require(block.timestamp >= campaign.startDate, "CrossEra: Campaign not ready to start");
        require(block.timestamp <= campaign.endDate, "CrossEra: Campaign expired");

        CampaignStatus oldStatus = campaign.status;
        campaign.status = CampaignStatus.Active;
        campaign.active = true;

        emit CampaignStatusChanged(
            campaignId,
            CampaignStatus.Active,
            oldStatus,
            msg.sender,
            block.timestamp
        );
    }

    /**
     * @notice Register app for a campaign
     * @param appId Application identifier
     * @param campaignId Campaign identifier
     * @param registrationFee Fee paid for registration (if any)
     * @param registrationData Additional registration data
     */
    function registerAppForCampaign(
        string memory appId,
        uint32 campaignId,
        uint256 registrationFee,
        bytes32 registrationData
    ) external appExists(appId) appOwnerOnly(appId) campaignExists(campaignId) whenNotPaused {
        CampaignCore storage campaign = campaigns[campaignId];
        require(campaign.active, "CrossEra: Campaign not active");
        require(block.timestamp >= campaign.startDate, "CrossEra: Campaign not started");
        require(block.timestamp <= campaign.endDate, "CrossEra: Campaign ended");

        // Handle registration fee if required
        if (registrationFee > 0) {
            rewardToken.safeTransferFrom(msg.sender, address(this), registrationFee);
        }

        emit AppRegisteredForCampaign(
            appId,
            campaignId,
            msg.sender,
            registrationFee,
            registrationData,
            block.timestamp
        );
    }

    // ==================== TRANSACTION PROCESSING FUNCTIONS ====================

    /**
     * @notice Process a verified transaction and calculate rewards
     * @param appId Application identifier
     * @param campaignId Campaign identifier
     * @param txHash Transaction hash
     * @param gasUsed Gas consumed by transaction
     * @param gasPrice Gas price used
     * @param transactionValue Transaction value
     * @param transactionType Type of transaction
     * @param additionalData Additional transaction metadata
     */
    function processTransaction(
        string memory appId,
        uint32 campaignId,
        bytes32 txHash,
        uint256 gasUsed,
        uint256 gasPrice,
        uint256 transactionValue,
        TransactionType transactionType,
        bytes32 additionalData
    ) external onlyVerifier appExists(appId) campaignExists(campaignId) whenNotPaused {
        require(!processedTransactions[txHash], "CrossEra: Transaction already processed");
        require(txHash != bytes32(0), "CrossEra: Invalid transaction hash");

        CampaignCore storage campaign = campaigns[campaignId];
        require(campaign.active, "CrossEra: Campaign not active");
        require(block.timestamp <= campaign.endDate, "CrossEra: Campaign ended");

        // Mark transaction as processed
        processedTransactions[txHash] = true;

        // Calculate reward based on gas usage
        uint256 feeGenerated = gasUsed * gasPrice;
        uint256 rewardAmount = calculateReward(feeGenerated, transactionValue);

        // Update claimable rewards
        claimableRewards[appId][campaignId] += rewardAmount;

        address developer = appOwners[appId];

        // Emit comprehensive transaction event
        emit TransactionProcessed(
            appId,
            campaignId,
            txHash,
            developer,
            gasUsed,
            gasPrice,
            transactionValue,
            feeGenerated,
            rewardAmount,
            transactionType,
            additionalData,
            block.timestamp
        );
    }

    /**
     * @notice Calculate reward amount based on transaction data
     * @param feeGenerated Gas fee generated
     * @param transactionValue Transaction value
     * @return rewardAmount Calculated reward amount
     */
    function calculateReward(uint256 feeGenerated, uint256 transactionValue) 
        internal 
        view 
        returns (uint256 rewardAmount) 
    {
        // Base reward calculation: 10% of gas fee or minimum reward, whichever is higher
        uint256 gasBasedReward = feeGenerated / 10;
        rewardAmount = gasBasedReward > minRewardAmount ? gasBasedReward : minRewardAmount;

        // Additional reward based on transaction value (optional enhancement)
        if (transactionValue > 0) {
            uint256 valueBasedReward = transactionValue / 1000; // 0.1% of transaction value
            rewardAmount += valueBasedReward;
        }
    }

    // ==================== REWARD CLAIMING FUNCTIONS ====================

    /**
     * @notice Claim rewards for an app in a specific campaign
     * @param appId Application identifier
     * @param campaignId Campaign identifier
     */
    function claimRewards(string memory appId, uint32 campaignId) 
        external 
        appExists(appId) 
        appOwnerOnly(appId) 
        campaignExists(campaignId) 
        nonReentrant 
        whenNotPaused 
        returns (uint256) 
    {
        uint256 claimableAmount = claimableRewards[appId][campaignId];
        require(claimableAmount > 0, "CrossEra: No rewards to claim");

        // Reset claimable amount first (reentrancy protection)
        claimableRewards[appId][campaignId] = 0;

        // Update campaign distributed rewards
        campaigns[campaignId].distributedRewards += claimableAmount;

        // Transfer rewards to developer
        rewardToken.safeTransfer(msg.sender, claimableAmount);

        bytes32 claimTxHash = keccak256(abi.encodePacked(block.timestamp, msg.sender, appId, campaignId));

        emit RewardClaimed(
            appId,
            campaignId,
            msg.sender,
            claimableAmount,
            campaigns[campaignId].distributedRewards,
            claimTxHash,
            block.timestamp
        );

        return claimableAmount;
    }

    // ==================== VIEW FUNCTIONS ====================

    /**
     * @notice Get claimable reward amount for an app in a campaign
     * @param appId Application identifier
     * @param campaignId Campaign identifier
     * @return Claimable reward amount
     */
    function getClaimableRewards(string memory appId, uint32 campaignId) 
        external 
        view 
        returns (uint256) 
    {
        return claimableRewards[appId][campaignId];
    }

    /**
     * @notice Check if a transaction has been processed
     * @param txHash Transaction hash
     * @return Whether transaction is processed
     */
    function isTransactionProcessed(bytes32 txHash) external view returns (bool) {
        return processedTransactions[txHash];
    }

    /**
     * @notice Get campaign core information
     * @param campaignId Campaign identifier
     * @return Campaign core data
     */
    function getCampaign(uint32 campaignId) external view returns (CampaignCore memory) {
        return campaigns[campaignId];
    }

    // ==================== ADMIN FUNCTIONS ====================

    /**
     * @notice Set minimum reward amount
     * @param _minRewardAmount New minimum reward amount
     */
    function setMinRewardAmount(uint256 _minRewardAmount) external onlyAdmin {
        bytes32 oldValue = bytes32(minRewardAmount);
        minRewardAmount = _minRewardAmount;

        emit ConfigurationUpdated(
            keccak256("MIN_REWARD_AMOUNT"),
            oldValue,
            bytes32(_minRewardAmount),
            msg.sender,
            block.timestamp
        );
    }

    /**
     * @notice Emergency pause the contract
     * @param reason Reason for pausing
     */
    function emergencyPause(string memory reason) external onlyAdmin {
        _pause();

        emit EmergencyAction(
            0, // pause
            msg.sender,
            reason,
            bytes32(0),
            block.timestamp
        );
    }

    /**
     * @notice Unpause the contract
     * @param reason Reason for unpausing
     */
    function emergencyUnpause(string memory reason) external onlyAdmin {
        _unpause();

        emit EmergencyAction(
            1, // unpause
            msg.sender,
            reason,
            bytes32(0),
            block.timestamp
        );
    }

    /**
     * @notice Emergency withdraw tokens (admin only)
     * @param token Token address to withdraw
     * @param amount Amount to withdraw
     * @param reason Reason for withdrawal
     */
    function emergencyWithdraw(
        address token,
        uint256 amount,
        string memory reason
    ) external onlyAdmin {
        IERC20(token).safeTransfer(msg.sender, amount);

        emit EmergencyAction(
            2, // emergency_withdraw
            msg.sender,
            reason,
            keccak256(abi.encodePacked(token, amount)),
            block.timestamp
        );
    }

    // ==================== ANALYTICS FUNCTIONS ====================

    /**
     * @notice Emit platform statistics snapshot
     * @param totalTransactionsProcessed Total transactions processed
     * @param totalVolumeProcessed Total volume processed
     * @param platformTVL Platform total value locked
     */
    function emitPlatformStats(
        uint256 totalTransactionsProcessed,
        uint256 totalVolumeProcessed,
        uint256 platformTVL
    ) external onlyAdmin {
        uint32 activeCampaignsCount = 0;
        uint256 totalRewardsDistributed = 0;

        // Count active campaigns and calculate total distributed rewards
        for (uint32 i = 1; i <= totalCampaigns; i++) {
            if (campaigns[i].active) {
                activeCampaignsCount++;
            }
            totalRewardsDistributed += campaigns[i].distributedRewards;
        }

        emit PlatformStatsSnapshot(
            totalDevelopers,
            totalApps,
            activeCampaignsCount,
            totalRewardsDistributed,
            totalTransactionsProcessed,
            totalVolumeProcessed,
            platformTVL,
            block.timestamp
        );
    }
}

contracts/MockXFIToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title MockXFIToken
 * @notice Mock XFI token for testing purposes on CrossFi testnet
 * @dev This contract simulates the XFI token functionality for development and testing
 */
contract MockXFIToken is ERC20, Ownable {
    uint8 private _decimals;

    constructor(
        string memory name,
        string memory symbol,
        uint256 initialSupply
    ) ERC20(name, symbol) Ownable(msg.sender) {
        _decimals = 18;
        _mint(msg.sender, initialSupply);
    }

    /**
     * @notice Mint tokens to a specific address (only owner)
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    /**
     * @notice Burn tokens from the caller's balance
     * @param amount Amount of tokens to burn
     */
    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }

    /**
     * @notice Get token decimals
     * @return Number of decimals
     */
    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    /**
     * @notice Faucet function for testnet - allows anyone to claim tokens
     * @dev In production, this would not exist
     */
    function faucet() external {
        require(balanceOf(msg.sender) < 1000 * 10**decimals(), "MockXFI: Already has enough tokens");
        _mint(msg.sender, 100 * 10**decimals()); // Mint 100 XFI tokens
    }

    /**
     * @notice Batch transfer tokens to multiple addresses
     * @param recipients Array of recipient addresses
     * @param amounts Array of amounts to transfer
     */
    function batchTransfer(address[] calldata recipients, uint256[] calldata amounts) external {
        require(recipients.length == amounts.length, "MockXFI: Arrays length mismatch");

        for (uint256 i = 0; i < recipients.length; i++) {
            transfer(recipients[i], amounts[i]);
        }
    }
}

